<script>
var iconMap={
  palms:"i504palms",
  smile:"502smile",
  star:"501star",
  heart:"505heart",
  thumbUp :"506thumbUp",
  thumbDown :"506thumbDown",
  search :"507search",
  direction:"508direction",
  moreFunc:"509moreFunc",
  home:"510home",
  refresh:"511refresh",
  attachment:"512attachment",
  fullscreen:"513fullscreen",
  forward:"515forward",
  comment:"516comment",
  upToTop:"517upToTop",
  footPrint:"518footPrint",
  header:"519header",
  suitcase:"520suitcase",
  goodscar:"521goodscar",
  uploader:"522uploader",
  cross:"523cross",
  doctorHat:'525doctorHat',
};
export default {
  name: "SvgIcons",
  props: {
    iconID:{
      type:String,
      default:"palms",
    },
    linecolor: {
      type: String,
      default: "red",
    },
    lineWidth: {
      type: String,
      default: "1px",
    },
    fillcolor: {
      type: String,
      default: "#222",
    },
    rotateAngle:{
        type: Number,
        default: 0,
    },
    iconWidth:{
        type: String,
    },
    iconHeight:{
        type: String,
        default: "0%",
    },
    shadowFn:{
      type: String,
      default:"",
    }
  },
  data(){
    // data内记录一些特殊元素的信息
    return {
      /*合掌相关的固定设置*/
      palmHand:"#ffca28", 
      palmHandLine:"#eda600",
      palmCloth:"#3088ce",
      palmClothLine:"#0370c8",
      /**其他特殊设置 */
    }
  },
  computed: {
    getIconID() {
      return "#"+iconMap[this.iconID];
    },
    getAngle() {
      var ret = "rotate(" + this.rotateAngle + ", 512, 512)";
      return ret;
    },
    getWidth() {
      return this.iconHeight;
    },
    getHeight() {
      return this.iconHeight;
    },
    get_filter(){
      return this.shadowFn;
    },
  },
};
</script>

